import React, { useState, useEffect } from 'react';
import { Share, Button, View, Text, ScrollView } from 'react-native';
import { useScrollToTop } from '@react-navigation/native';
import styles from './style';

import Icon from 'react-native-vector-icons/MaterialCommunityIcons';

const Story = () => {
    const [joke, setJoke] = useState("use click me button for next joke");
    const newjoke = async () => {
        let rnum = await Math.floor(Math.random() * offline.length);
        const joke = await offline[rnum].joke
        // await console.log(joke)
        setJoke(joke + offline[rnum].id)

    };
    useEffect(() => {
        newjoke()
    }, [])

    const OnShare = async () => {
        try {
            const result = await Share.share({
                message: joke,
            });
        } catch (error) {
            alert(error.message);
        }
    }


    const ref = React.useRef(null);
    useScrollToTop(ref);

    const sizeis = 46;
    return (
        <>
            <ScrollView contentContainerStyle={styles.background} ref={ref}>

                <Text style={styles.text}> {joke}</Text>
                <Text></Text>
                <Text></Text>
                <Text></Text>
          
          
            

            </ScrollView>
            <View style={styles.appButtonContainer}>
                <Button style={styles.appButton} title='                 NEXT STORY >>               ' onPress={newjoke} />
                <Text>   </Text>
                <Icon name="whatsapp" style={styles.sharebtn} color='green' onPress={OnShare} size={sizeis} />
            </View>
            <Text></Text>

        </>
    );
}
